mapper.select:
select p.id,p.name from sys_user_position up
                            inner join sys_position p on up.position_id=p.id
where up.user_id=#{userId}

select 1 from sys_application_auth
where business_id=#{businessId}
and module=#{module}
and operate = #{operate}
and authority in

    #{baseAuth.auth}

limit 1
;
